	/**
	* Makes a random 2D structure of arrays and objects, with random binary data roughly up to the dimension size specified.
	*/
	public static function generateRandomStructure($iSize=5){
		$a = array();

		for($i = 0; $i < rand(0, $iSize); $i++){
			$a2 = array();
			for($j = 0; $j < rand(0, $iSize); $j++){
				// random string
				$a2["k$j"]  = md5(substr(str_shuffle("0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ"), 0, $iSize));

			}
			$a[] = (object)$a2;
		}
		return $a;
	}
	
	/**
	 * REturns one of a set of known data structures.
	 * use isKnownStructure() to test if what you have is one of these.
	 * Handy for testing data persistance, without knowing exacvtly what data is stored,
	 * just something correct. e.g. from concurrent processes.
	 */
	public function getKnownStructure(){
		return self::$aKnownStructures[rand(0, count(self::$aKnownStructures)-1)];
	}
	
	public function isKnownStructure($obj){
		return in_array($obj, self::$aKnownStructures);
